class Loading {
    constructor() {
        this.init()
    }
    init() {
        this.img = null;
        this.mask = null;
        Element.prototype.addStyle = function (styles) {
            for (var property in styles) {
                this.style[property] = styles[property]
            }
        }
        this.createDom ()
    }
    createDom() {
        this.img = document.createElement('img')
        // this.img.src = 'data:image/svg+xml;base64,'
        this.img.src = require('./loading.gif')
        
        var width = 34;
        this.img.addStyle({
            'position': 'fixed', 'top': '50%', 'left': '50%',
            'margin-left': width/-2 + 'px',
            'margin-top': width/-2 + 'px',
            'width': width + 'px',
            'height': width + 'px'
        })

        this.mask = document.createElement('div')
        this.mask.addStyle({
            'display': 'none',
            'position': 'fixed', 'top': '0', 'bottom': '0', 'left': '0', 'right': '0',
            'background-color': 'rgba(255,255,255,.7)',
            'blur': '(10px)'
        })
        this.mask.appendChild(this.img)
        document.body.appendChild(this.mask)

        this.show()
    }
    show() {
        this.overBody(true)
        this.mask.style.display = 'block'
    }
    hide() {
        this.overBody(false)
        this.mask.style.display = 'none'
    }
    eventDefault(e) {
        e.preventDefault()
        e.stopPropagation()
        // console.log('eventDefault')
    }
    overBody(type) {
        document.documentElement.style.overflow = type ? 'hidden' : ''
        type ? document.addEventListener('touchmove', this.eventDefault)
             : document.removeEventListener('touchmove', this.eventDefault)
    }
}

let loading = new Loading()

if (typeof module === "object" && typeof module.exports === "object") {
    module.exports = loading
} else {
    window.loading = loading
}
